const student_id=sessionStorage.getItem("id"),token=sessionStorage.getItem("token"),get_profile_url="https://softeng.jbtabz.com/student/"+student_id;async function fetchUserName(){const e=await fetch(get_profile_url,{headers:{"X-Session-Token":token}}),t=await e.json();document.getElementById("welcome").innerHTML="WELCOME "+t.first_name.toUpperCase()+" !"}window.onload=function(){fetchUserName()};